mapper.insert:
INSERT INTO YAGL_DWJZXX(jzid,jzmc,jzlx,jzwz,lon,lat,xqxclx,yjddsc,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() jzid, A.* FROM (
SELECT #{item.jzid,jdbcType=VARCHAR} jzid,#{item.jzmc,jdbcType=VARCHAR} jzmc,#{item.jzlx,jdbcType=VARCHAR} jzlx,#{item.jzwz,jdbcType=VARCHAR} jzwz,#{item.lon,jdbcType=DOUBLE} lon,#{item.lat,jdbcType=DOUBLE} lat,#{item.xqxclx,jdbcType=VARCHAR} xqxclx,#{item.yjddsc,jdbcType=DOUBLE} yjddsc,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_DWJZXX
jzid=#{jzid}, jzmc=#{jzmc}, jzlx=#{jzlx}, jzwz=#{jzwz}, lon=#{lon}, lat=#{lat}, xqxclx=#{xqxclx}, yjddsc=#{yjddsc}, delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE JZID = #{jzid}

mapper.select:
SELECT tt.*, s.pic_blob photo FROM ( SELECT , c2.code_name jzlxmc,
FROM YAGL_DWJZXX t LEFT JOIN YAGL_DWJZXX_CGL t_cgl ON t.jzid = t_cgl.jzid AND t_cgl.DELETE_FLAG = 'N'
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZFL' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c2.CODEID = c1.CODEID And c2.DELETE_FLAG = 'N' and t.jzlx = c2.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
)tt,SYS_CODELIST_PICTURE s WHERE s.pic_type = 'JZFL' and pic_value = #{jzlx}

		SELECT FQID
 		FROM YAGL_DX_ZDDW_JZFQ WHERE DELETE_FLAG = 'N'
 		AND ZDDWID = #{value}
