mapper.select:

SELECT 

FROM YAGL_WL_XFZBXX_CL t 
WHERE t.DELETE_FLAG = 'N' AND  t.UUID = #{value}

mapper.insert:
INSERT INTO YAGL_WL_XFZBXX_CL(uuid,zbid,clid,clmc,clzzs,bz,delete_flag,datasource,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.zbid,jdbcType=VARCHAR} zbid,#{item.clid,jdbcType=VARCHAR} clid,#{item.clmc,jdbcType=VARCHAR} clmc,#{item.clzzs,jdbcType=DOUBLE} clzzs,#{item.bz,jdbcType=VARCHAR} bz,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_WL_XFZBXX_CL
uuid=#{uuid}, zbid=#{zbid}, clid=#{clid}, clmc=#{clmc}, clzzs=#{clzzs}, bz=#{bz}, delete_flag='N', datasource=#{datasource}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}
