mapper.select:
SELECT , c2.code_name jzlxmc FROM YAGL_DWJZXX t LEFT JOIN YAGL_DWJZXX_JZL t_jzl ON t.jzid = t_jzl.jzid AND t_jzl.DELETE_FLAG = 'N'
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZFL' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.jzlx = c2.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
AND (t.jzmc LIKE concat(concat('%',#{jzmc}),'%') OR t.jzwz LIKE concat(concat('%',#{jzmc}),'%'))
AND t.jzlx = #{jzlx}
AND t_jzl.jzsyxz LIKE concat(#{jzl_jzsyxz},'%')
AND t_jzl.jzjg = #{jzl_jzjg}
AND t_jzl.dsgd <= #{jzl_dsgd_max}
AND t_jzl.dsgd > #{jzl_dsgd_min}

SELECT , c2.code_name jzlxmc FROM YAGL_DWJZXX t LEFT JOIN YAGL_DWJZXX_ZZL t_zzl ON t.jzid = t_zzl.jzid AND t_zzl.DELETE_FLAG = 'N'
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'JZFL' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.jzlx = c2.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
AND (t.jzmc LIKE concat(concat('%',#{jzmc}),'%') OR t.jzwz LIKE concat(concat('%',#{jzmc}),'%'))
AND t.jzlx = #{jzlx}
AND t_zzl.jzjg = #{zzl_jzjg}

SELECT t.uuid uuid, t.dwmc dxmc, t.dwdz dxdz, c9.DZMC xfgx, '重点单位' yadxType FROM YAGL_DX_ZDDW t
LEFT JOIN YAGL_XFDZ c9 ON (t.MHDZID=c9.DZID AND c9.DELETE_FLAG = 'N') WHERE t.DELETE_FLAG = 'N'
and t.dwmc LIKE concat(concat('%',#{dxmc}),'%')
and c9.dzbm like concat(#{xfgx},'%')
and t.dwxz = #{dwxz}
and t.xzqh LIKE concat(RTRIM(#{xzqh},0),'%')
and t.fhdj = #{fhdj}
and t.jzfl = #{jzfl}
AND rownum <= 100 ORDER BY t.XZQH,t.DWXZ,t.DWMC ASC
