mapper.select:
SELECT

FROM SYS_PERMISSION t
WHERE t.DELETE_FLAG = 'N' AND  t.permissionid = #{value}

mapper.insert:
INSERT INTO SYS_PERMISSION(permissionid,permissionname,permissioninfo,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,delete_flag) SELECT SYS_GUID() permissionid, A.* FROM (
SELECT #{item.permissionname,jdbcType=VARCHAR} permissionname,#{item.permissioninfo,jdbcType=VARCHAR} permissioninfo,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,SYSDATE alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,'N' delete_flag FROM DUAL
) A

mapper.update:
UPDATE SYS_PERMISSION
permissionid=#{permissionid}, permissionname=#{permissionname}, permissioninfo=#{permissioninfo}, create_id=#{createId}, create_name=#{createName}, create_time=#{createTime}, alter_id=#{alterId}, alter_name=#{alterName}, alter_time=#{alterTime}, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, delete_flag=#{deleteFlag}
WHERE permissionid = #{permissionid}
