mapper.select:

SELECT 

FROM SYS_USER t 
WHERE t.DELETE_FLAG = 'N' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO SYS_USER(pkid,username,password,realname,birth,sex,deptid,position,phone,mobile,email,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,delete_flag,salt) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.username,jdbcType=VARCHAR} username,#{item.password,jdbcType=VARCHAR} password,#{item.realname,jdbcType=VARCHAR} realname,#{item.birth,jdbcType=DATE} birth,#{item.sex,jdbcType=VARCHAR} sex,#{item.deptid,jdbcType=VARCHAR} deptid,#{item.position,jdbcType=VARCHAR} position,#{item.phone,jdbcType=VARCHAR} phone,#{item.mobile,jdbcType=VARCHAR} mobile,#{item.email,jdbcType=VARCHAR} email,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,SYSDATE alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,'N' delete_flag,#{item.salt,jdbcType=VARCHAR} salt FROM DUAL
) A

mapper.update:
UPDATE SYS_USER
pkid=#{pkid}, username=#{username}, password=#{password}, realname=#{realname}, birth=TO_DATE(#{birth},'yyyy-MM-dd HH24:mi:ss'), sex=#{sex}, deptid=#{deptid}, position=#{position}, phone=#{phone}, mobile=#{mobile}, email=#{email}, create_id=#{createId}, create_name=#{createName}, create_time=TO_DATE(#{createTime},'yyyy-MM-dd HH24:mi:ss'), alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', salt=#{salt}
WHERE PKID = #{pkid}

mapper.insert:
INSERT INTO SYS_USER_ROLE(pkid,userid,roleid,create_id,create_name,create_time,delete_flag) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.userid,jdbcType=VARCHAR} userid,#{item.roleid,jdbcType=VARCHAR} roleid,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,'N' delete_flag FROM DUAL
) A
