mapper.select:

SELECT 

FROM SYS_ACCOUNT t 
WHERE t.DELETE_FLAG = 'N' AND  t.userid = #{value}

mapper.insert:
INSERT INTO SYS_ACCOUNT(userid,username,password,salt,delete_flag,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,realname) SELECT SYS_GUID() userid, A.* FROM (
SELECT #{item.userid,jdbcType=VARCHAR} userid,#{item.username,jdbcType=VARCHAR} username,#{item.password,jdbcType=VARCHAR} password,#{item.salt,jdbcType=VARCHAR} salt,'N' delete_flag,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,#{item.alterTime,jdbcType=TIMESTAMP} alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=TIMESTAMP} reserve3,#{item.realname,jdbcType=VARCHAR} realname FROM DUAL
) A

mapper.update:
UPDATE SYS_ACCOUNT
userid=#{userid}, username=#{username}, password=#{password}, salt=#{salt}, delete_flag='N', create_id=#{createId}, create_name=#{createName}, create_time=#{createTime}, alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, realname=#{realname}
WHERE userid = #{userid}

mapper.insert:
INSERT INTO SYS_ACCOUNT_ROLE(pkid,userid,roleid,create_id,create_name,create_time,delete_flag) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.userid,jdbcType=VARCHAR} userid,#{item.roleid,jdbcType=VARCHAR} roleid,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,'N' delete_flag FROM DUAL
) A
