mapper.select:

SELECT 

FROM SYS_CODELIST_PICTURE t 
WHERE t.DELETE_FLAG = 'N' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO SYS_CODELIST_PICTURE(pkid,pic_type,pic_typename,pic_value,pic_name,pic_blob,delete_flag,remark,create_id,create_name,create_time,alter_id,alter_name,alter_time) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.picType,jdbcType=VARCHAR} pic_type,#{item.picTypename,jdbcType=VARCHAR} pic_typename,#{item.picValue,jdbcType=VARCHAR} pic_value,#{item.picName,jdbcType=VARCHAR} pic_name,#{item.picBlob,jdbcType=VARCHAR} pic_blob,'N' delete_flag,#{item.remark,jdbcType=VARCHAR} remark,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,SYSDATE alter_time FROM DUAL
) A

mapper.update:
UPDATE SYS_CODELIST_PICTURE
pkid=#{pkid}, pic_type=#{picType}, pic_typename=#{picTypename}, pic_value=#{picValue}, pic_name=#{picName}, delete_flag='N', remark=#{remark}, create_id=#{createId}, create_name=#{createName}, create_time=TO_DATE(#{createTime},'yyyy-MM-dd HH24:mi:ss'), alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}

mapper.select:
SELECT

FROM SYS_CODELIST_PICTURE t  WHERE t.DELETE_FLAG = 'N'

	and t.pic_type = #{picType}


	and t.pic_name  = #{picName}

ORDER BY t.pic_type,t.pic_value
