mapper.select:
SELECT

FROM SYS_PERMISSION t
WHERE t.DELETE_FLAG = 'N' AND  t.permissionid = #{value}

mapper.insert:
INSERT INTO SYS_PERMISSION(permissionid,permissionname,permissioninfo,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,delete_flag) SELECT SYS_GUID() permissionid, A.* FROM (
SELECT #{item.permissionname,jdbcType=VARCHAR} permissionname,#{item.permissioninfo,jdbcType=VARCHAR} permissioninfo,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,#{item.alterTime,jdbcType=TIMESTAMP} alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,'N' delete_flag FROM DUAL
) A

mapper.update:
UPDATE SYS_PERMISSION
permissionid=#{permissionid}, permissionname=#{permissionname}, permissioninfo=#{permissioninfo}, create_id=#{createId}, create_name=#{createName}, create_time=#{createTime}, alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss') reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, delete_flag=#{deleteFlag}
WHERE permissionid = #{permissionid}

mapper.select:
SELECT DISTINCT t1.permissionid, CASE WHEN INSTR(t1.resourcename,'/',1,3) != 0 THEN SUBSTR(t1.resourcename,2,INSTR(t1.resourcename,'/',1,3)-2) ||':'||t3.permissionname ELSE SUBSTR(t1.resourcename, 2)||':'||t3.permissionname END permissionname FROM( SELECT r.resourceid,r.resourcename,r.parentid,c.permissionid FROM SYS_RESOURCE_PERMISSION c LEFT JOIN SYS_RESOURCE r ON c.resourceid = r.resourceid WHERE r.delete_flag = 'N' AND c.delete_flag = 'N' AND r.resourceid IN
#{item.resourceid,jdbcType=VARCHAR}
) t1 LEFT JOIN (SELECT permissionid,permissionname FROM SYS_PERMISSION WHERE delete_flag = 'N') t3 ON t1.permissionid = t3.permissionid WHERE t1.parentid != '-1'
