mapper.select:

SELECT 

FROM SYS_RESOURCE t 
WHERE t.DELETE_FLAG = 'N' AND  t.RESOURCEID = #{value}

mapper.insert:
INSERT INTO SYS_RESOURCE(resourceid,resourcename,resourceinfo,parentid,seqno,icon,type,delete_flag,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3) SELECT SYS_GUID() resourceid, A.* FROM (
SELECT #{item.resourceid,jdbcType=VARCHAR} resourceid,#{item.resourcename,jdbcType=VARCHAR} resourcename,#{item.resourceinfo,jdbcType=VARCHAR} resourceinfo,#{item.parentid,jdbcType=VARCHAR} parentid,#{item.seqno,jdbcType=DOUBLE} seqno,#{item.icon,jdbcType=VARCHAR} icon,#{item.type,jdbcType=VARCHAR} type,'N' delete_flag,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,#{item.alterTime,jdbcType=TIMESTAMP} alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3 FROM DUAL
) A

mapper.update:
UPDATE SYS_RESOURCE
resourcename=#{resourcename}, resourceinfo=#{resourceinfo}, parentid=#{parentid}, seqno=#{seqno}, icon=#{icon}, type=#{type}, delete_flag='N', create_id=#{createId}, create_name=#{createName}, create_time=TO_DATE(#{createTime},'yyyy-MM-dd HH24:mi:ss'), alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss')
WHERE resourceid = #{resourceid}

mapper.insert:
INSERT INTO SYS_RESOURCE_PERMISSION(pkid,resourceid,permissionid,create_id,create_name,create_time,delete_flag) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.resourceid,jdbcType=VARCHAR} resourceid,#{item.permissionid,jdbcType=VARCHAR} permissionid,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,'N' delete_flag FROM DUAL
) A

mapper.select:
SELECT DISTINCT r.resourceid,r.resourcename,r.resourceinfo,r.parentid,r.seqno,r.icon， r.type, r.create_name createName, TO_CHAR(r.create_time,'yyyy-MM-dd HH24:mi:ss') createTime, r.alter_name alterName, TO_CHAR(r.alter_time,'yyyy-MM-dd HH24:mi:ss') alterTime FROM SYS_RESOURCE r LEFT JOIN SYS_ROLE_RESOURCE c ON r.resourceid = c.resourceid WHERE r.delete_flag = 'N'
AND roleid IN
#{item.roleid,jdbcType=VARCHAR}
AND parentid = #{parentid}
AND type= #{type}
ORDER BY seqno
