mapper.select:

SELECT 

FROM SYS_ROLE t 
WHERE t.DELETE_FLAG = 'N' AND  t.roleid = #{value}

mapper.insert:
INSERT INTO SYS_ROLE(roleid,rolename,roleinfo,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,delete_flag) SELECT SYS_GUID() roleid, A.* FROM (
SELECT #{item.rolename,jdbcType=VARCHAR} rolename,#{item.roleinfo,jdbcType=VARCHAR} roleinfo,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,#{item.alterTime,jdbcType=TIMESTAMP} alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,'N' delete_flag FROM DUAL
) A

mapper.update:
UPDATE SYS_ROLE
roleid=#{roleid}, rolename=#{rolename}, roleinfo=#{roleinfo}, create_id=#{createId}, create_name=#{createName}, create_time=#{createTime}, alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, delete_flag=#{deleteFlag}
WHERE roleid = #{roleid}

mapper.insert:
INSERT INTO SYS_ROLE_RESOURCE(pkid,roleid,resourceid,create_id,create_name,create_time,delete_flag) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.roleid,jdbcType=VARCHAR} roleid,#{item.resourceid,jdbcType=VARCHAR} resourceid,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,'N' delete_flag FROM DUAL
) A
