mapper.select:

SELECT 

FROM SYS_USER t 
WHERE t.DELETE_FLAG = 'N' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO SYS_USER(pkid,userid,realname,birth,sex,deptid,position,phone,mobile,email,delete_flag,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,organization_id) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.userid,jdbcType=VARCHAR} userid,#{item.realname,jdbcType=VARCHAR} realname,#{item.birth,jdbcType=DATE} birth,#{item.sex,jdbcType=VARCHAR} sex,#{item.deptid,jdbcType=VARCHAR} deptid,#{item.position,jdbcType=VARCHAR} position,#{item.phone,jdbcType=VARCHAR} phone,#{item.mobile,jdbcType=VARCHAR} mobile,#{item.email,jdbcType=VARCHAR} email,'N' delete_flag,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,#{item.alterTime,jdbcType=TIMESTAMP} alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,#{item.organizationId,jdbcType=VARCHAR} organization_id FROM DUAL
) A

mapper.update:
UPDATE SYS_USER
pkid=#{pkid}, userid=#{userid}, realname=#{realname}, birth=TO_DATE(#{birth},'yyyy-MM-dd HH24:mi:ss'), sex=#{sex}, deptid=#{deptid}, position=#{position}, phone=#{phone}, mobile=#{mobile}, email=#{email}, delete_flag='N', create_id=#{createId}, create_name=#{createName}, create_time=TO_DATE(#{createTime},'yyyy-MM-dd HH24:mi:ss'), alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss') organization_id=#{organizationId},
WHERE PKID = #{pkid}

UPDATE SYS_USER
alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), organization_id=''
WHERE organization_id = #{organizationId}
